import { Component } from "react";
import Form from './Form'
import {nanoid} from 'nanoid'

export class App extends Component {
  state = {
    contacts: [],
    filter: '',
    name: '',
    number: ''
  }

  addContact = contact => {
    this.setState(prevState => ({ contacts: [{ id: nanoid(), ...contact }, ...prevState.contacts],}))
  }

  
  render () {
    return (
      <Form/>
    )
  }
}
